import './App.css';
import io from 'socket.io-client';
import React, { useEffect, useState } from 'react';
const socket = io.connect('http://localhost:3001');

function App() {
  const [message, setMessage] = useState('');
  const [totalclient, setTotalclient] = useState(0);
  const [messages, setMessages] = useState([]);
  const [pairedDevice, setPairedDevice] = useState('');

  const sendMessage = () => {
    const messageWithSender = { sender: 'You', message: message }; // Attach the sender's name to the message
    socket.emit('send_message', messageWithSender);
    setMessages([...messages, messageWithSender]);
    setMessage(''); // Clear the input field after sending the message
  };

  useEffect(() => {
    socket.on('receive_message', (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    socket.on('total-num', (total_num) => {
      setTotalclient(total_num);
    });

    socket.on('paired', (deviceId) => {
      setPairedDevice(deviceId);
    });

    return () => {
      socket.off('receive_message');
      socket.off('total-num');
      socket.off('paired');
    };
  }, []);

  return (
    <div className="App">
      <h2>Total people: {totalclient}</h2>
      {pairedDevice && <h3>Paired with: {pairedDevice}</h3>}
      <div className="message-container">
        {messages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.sender ? msg.sender + ': ' : 'Unknown Sender: '}</strong>
            {msg.message}
          </div>
        ))}
      </div>
      <input
        type="text"
        placeholder="Message.."
        value={message}
        onChange={(event) => {
          setMessage(event.target.value);
        }}
      />
      <button onClick={sendMessage}>Send Message</button>
    </div>
  );
}

export default App;
